import React from 'react'
import styled from 'styled-components'

const options = [
    {
        'name' : 'Category',
        'component' : 'Category',
    },
    {
        'name' : 'Description',
        'component' : 'Description',
    },
    {
        'name' : 'Location',
        'component' : 'Location',
    },
    {
        'name' : 'Benefits',
        'component' : 'Benefits',
    },
    {
        'name' : 'Intro Video',
        'component' : 'Intro_Video',
    },
    {
        'name' : 'Mentor Details',
        'component' : 'Mentor_Details',
    },
    {
        'name' : 'Recommended Roles',
        'component' : 'Recommended_Roles',
    },
    {
        'name' : 'Web Links & Resources',
        'component' : 'Resources',
    },
]

const modules = () => {
  return (
    <Wrapper className='col-12' >
        <Module className='col-12 col-md-6'>
            {options.map(function(option, index){
                return (
                    <ModuleItem className='my-2 p-2' >
                        <h6>{option.name}</h6>
                        <div><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="grey" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-chevrons-right"><polyline points="13 17 18 12 13 7"></polyline><polyline points="6 17 11 12 6 7"></polyline></svg></div>
                    </ModuleItem>
                )
            })}
        </Module>
    </Wrapper>
  )
}

export default modules

const Wrapper = styled.div`

`

const Module = styled.div`

`

const ModuleItem = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    background-color: white;
    border-radius: 1rem;
`

